def get_unverified_cert(cert_type, cert_auth_title, stamp_png, tabular_info, issued_person_name="tbd"):
    badge = cert_template['badge']
    if cert_type == 'birth':
        badge['name'] = 'Birth Certificate'
    elif cert_type == 'death':
        badge['name'] = 'Death Certificate'
    badge['description'] = tabular_info
    badge['signatureLines'] = [{
            "jobTitle":
            cert_auth_title,
            "name":
            "Hospital/MCO Signature and Stamp png",
            "type": ["SignatureLine", "Extension"],
            "image":
            "data:image/png;base64," + stamp_png
        }]
    cert_template['badge'] = badge
    cert_template['recipientProfile'] = {
        "publicKey": "ecdsa-koblitz-pubkey:mtr98kany9G1XYNU74pRnfBQmaCg2FZLmc",
        "name": issued_person_name,
        "type": ["RecipientProfile", "Extension"]
    }
    return cert_template